const keywords = ["system design", "ai", "backend", "architecture"];

const isEnglish = (text) => /^[\x00-\x7F]*$/.test(text);
return items
  .filter(item => {
    const text = ((item.json.title || "") + " " + (item.json.contentSnippet || "")).toLowerCase();
    return keywords.some(k => text.includes(k));
  })
  .filter(item => {
    const text = (item.json.title || "") + " " + (item.json.contentSnippet || "");
    return isEnglish(text);
  })
  .sort((a, b) => new Date(b.json.isoDate) - new Date(a.json.isoDate))
  .slice(0, 2);

